<script lang="ts">
import { defineComponent, type PropType } from 'vue'
import { useI18n } from 'vue-i18n'
import type { LevelSetting } from '@/data/benefits'

export default defineComponent({
  name: 'BenefitTable',

  props: {
    benefits: {
      type: Array as PropType<LevelSetting[]>,
      required: true
    }
  },

  emits: ['edit', 'delete'],

  setup(props, { emit }) {
    const { t } = useI18n()

    const handleEdit = (benefit: LevelSetting) => {
      emit('edit', benefit)
    }

    const handleDelete = (levelId: number) => {
      emit('delete', levelId)
    }

    return {
      handleEdit,
      handleDelete,
      t
    }
  }
})
</script>

