<!-- Generated by Cursor -->
<template>
  <Modal
    :open="visible"
    :title="isEdit ? 'Chỉnh Sửa Cấp Bậc' : 'Thêm Cấp Bậc Mới'"
    @cancel="handleCancel"
    @ok="handleOk"
    :confirmLoading="loading"
    :okText="isEdit ? 'Cập Nhật' : 'Thêm Mới'"
    :cancelText="'Hủy'"
    :maskClosable="false"
    class="level-modal"
  >
    <Spin :spinning="loading">
      <Form
        :model="formState"
        :rules="rules"
        ref="formRef"
        layout="vertical"
      >
        <!-- Name Field -->
        <FormItem 
          label="Tên Cấp Bậc" 
          name="Name"
          :validateStatus="errors.Name ? 'error' : ''"
          :help="errors.Name"
          required
        >
          <Input 
            v-model:value="formState.Name" 
            @blur="validateField('Name')"
            placeholder="Nhập tên cấp bậc"
            class="custom-input"
          />
        </FormItem>

        <!-- Rank Field -->
        <FormItem 
          label="Thứ Hạng" 
          name="rank"
          :validateStatus="errors.rank ? 'error' : ''"
          :help="errors.rank"
          required
        >
          <InputNumber 
            v-model:value="formState.rank"
            :min="0"
            class="w-full custom-input"
            @blur="validateField('rank')"
            placeholder="Nhập thứ hạng"
          />
        </FormItem>

        <!-- Threshold Amount Field -->
        <FormItem 
          label="Số Tiền Ngưỡng" 
          name="thresholdAmount"
          :validateStatus="errors.thresholdAmount ? 'error' : ''"
          :help="errors.thresholdAmount"
          required
        >
          <InputNumber
            v-model:value="formState.thresholdAmount"
            :min="0"
            class="w-full custom-input"
            @blur="validateField('thresholdAmount')"
            :formatter="value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')"
            :parser="value => value!.replace(/\$\s?|(,*)/g, '')"
            placeholder="Nhập số tiền ngưỡng"
          />
        </FormItem>

        <!-- Duration Field -->
        <FormItem 
          label="Thời Hạn (Tháng)" 
          name="durationExpired"
          :validateStatus="errors.durationExpired ? 'error' : ''"
          :help="errors.durationExpired"
          required
        >
          <InputNumber
            v-model:value="formState.durationExpired"
            :min="1"
            :max="12"
            class="w-full custom-input"
            @blur="validateField('durationExpired')"
            placeholder="Nhập thời hạn"
          />
        </FormItem>

      </Form>
    </Spin>
  </Modal>
</template>

<script lang="ts" setup>
import { ref, watch } from 'vue';
import { 
  Modal,
  Form,
  FormItem,
  Input,
  InputNumber,
  message,
  Spin
} from 'ant-design-vue';
import { ExclamationCircleOutlined } from '@ant-design/icons-vue';
import { createVNode } from 'vue';
import type { FormInstance } from 'ant-design-vue';
import { type LevelSetting, defaultFormState } from '@/api/types/levelUpDownRules';
import { useLevelSettingStore } from '@/stores/levelUpDownRules';
import { levelUpDownRulesApi, ApiError } from '@/api/services/levelUpDownRulesApi';
const levelSettingStore = useLevelSettingStore();

// Props
const props = defineProps<{
  visible: boolean;
  isEdit: boolean;
  initialData?: LevelSetting;
  websiteId: number;
}>();

const emit = defineEmits<{
  (e: 'update:visible', value: boolean): void;
  (e: 'submit', data: LevelSetting): void;
  (e: 'success'): void;
}>();

const modal = Modal.useModal();
const loading = ref(false);
const formRef = ref<FormInstance>();
const formState = ref<LevelSetting>({ ...defaultFormState });
const errors = ref<Record<string, string>>({});

// Validation rules
const rules = {
  Name: [
    { required: true, message: 'Vui lòng nhập tên cấp bậc' },
    { max: 50, message: 'Tên cấp bậc không được quá 50 ký tự' }
  ],
  rank: [
    { required: true, message: 'Vui lòng nhập thứ hạng' },
    { type: 'number', min: 0, message: 'Thứ hạng phải lớn hơn hoặc bằng 0' }
  ],
  thresholdAmount: [
    { required: true, message: 'Vui lòng nhập số tiền ngưỡng' },
    { type: 'number', min: 0, message: 'Số tiền ngưỡng phải lớn hơn hoặc bằng 0' }
  ],
  durationExpired: [
    { required: true, message: 'Vui lòng nhập thời hạn' },
    { type: 'number', min: 1, max: 12, message: 'Thời hạn phải từ 1-12 tháng' }
  ]
};

// Validation functions
const validateField = async (field: string) => {
  errors.value[field] = '';
  
  switch (field) {
    case 'Name':
      if (!formState.value.Name.trim()) {
        errors.value.Name = 'Vui lòng nhập tên cấp bậc';
        return false;
      }
      if (formState.value.Name.length > 50) {
        errors.value.Name = 'Tên cấp bậc không được quá 50 ký tự';
        return false;
      }
      const nameExists = await levelUpDownRulesApi.checkNameExists(
        props.websiteId,
        formState.value.Name,
        props.isEdit ? formState.value.id : undefined
      );
      if (nameExists) {
        errors.value.Name = `Tên cấp bậc "${formState.value.Name}" đã tồn tại`;
        return false;
      }
      break;

    case 'rank':
      if (formState.value.rank === null || formState.value.rank === undefined) {
        errors.value.rank = 'Vui lòng nhập thứ hạng';
        return false;
      }
      const rankExists = await levelUpDownRulesApi.checkRankExists(
        props.websiteId,
        formState.value.rank,
        props.isEdit ? formState.value.id : undefined
      );
      if (rankExists) {
        errors.value.rank = `Thứ hạng ${formState.value.rank} đã tồn tại`;
        return false;
      }
      break;

    case 'thresholdAmount':
      if (!formState.value.thresholdAmount) {
        errors.value.thresholdAmount = 'Vui lòng nhập số tiền ngưỡng';
        return false;
      }
      if (formState.value.thresholdAmount < 0) {
        errors.value.thresholdAmount = 'Số tiền ngưỡng không được âm';
        return false;
      }
      break;

    case 'durationExpired':
      if (!formState.value.durationExpired) {
        errors.value.durationExpired = 'Vui lòng nhập thời hạn';
        return false;
      }
      if (formState.value.durationExpired < 1 || formState.value.durationExpired > 12) {
        errors.value.durationExpired = 'Thời hạn phải từ 1-12 tháng';
        return false;
      }
      break;
  }
  
  return true;
};

const validateName = async () => {
  errors.value.Name = '';
  
  if (!formState.value.Name.trim()) {
    errors.value.Name = 'Vui lòng nhập tên cấp bậc';
    return false;
  }

  if (formState.value.Name.length > 50) {
    errors.value.Name = 'Tên cấp bậc không được quá 50 ký tự';
    return false;
  }

  // Kiểm tra tên trùng lặp
  try {
    const nameExists = await levelUpDownRulesApi.checkNameExists(
      props.websiteId,
      formState.value.Name,
      props.isEdit ? formState.value.levelId : undefined
    );
    if (nameExists) {
      errors.value.Name = `Tên cấp bậc "${formState.value.Name}" đã tồn tại`;
      return false;
    }
  } catch (error) {
    console.error('Error checking name:', error);
    errors.value.Name = 'Có lỗi xảy ra khi kiểm tra tên';
    return false;
  }

  return true;
};

const validateRank = async () => {
  errors.value.rank = '';

  if (formState.value.rank === null || formState.value.rank === undefined) {
    errors.value.rank = 'Vui lòng nhập thứ hạng';
    return false;
  }

  if (formState.value.rank < 0) {
    errors.value.rank = 'Thứ hạng không được âm';
    return false;
  }

  // Kiểm tra rank trùng lặp
  try {
    const rankExists = await levelUpDownRulesApi.checkRankExists(
      props.websiteId,
      formState.value.rank,
      props.isEdit ? formState.value.levelId : undefined
    );
    if (rankExists) {
      errors.value.rank = `Thứ hạng ${formState.value.rank} đã tồn tại`;
      return false;
    }
  } catch (error) {
    console.error('Error checking rank:', error);
    errors.value.rank = 'Có lỗi xảy ra khi kiểm tra thứ hạng';
    return false;
  }

  return true;
};

// Thêm watch để validate khi giá trị thay đổi
watch(() => formState.value.Name, () => {
  validateName();
}, { debounce: 300 });

watch(() => formState.value.rank, () => {
  validateRank();
});

const validateForm = async () => {
  const nameValid = await validateName();
  const rankValid = await validateRank();
  
  // Validate các trường khác
  let isValid = nameValid && rankValid;
  
  if (!formState.value.thresholdAmount && formState.value.thresholdAmount !== 0) {
    errors.value.thresholdAmount = 'Vui lòng nhập số tiền ngưỡng';
    isValid = false;
  }

  if (!formState.value.durationExpired) {
    errors.value.durationExpired = 'Vui lòng nhập thời hạn';
    isValid = false;
  }

  return isValid;
};

const { confirm } = Modal;

const handleOk = () => {
  if (props.isEdit) {
    confirm({
      title: 'Xác nhận cập nhật',
      icon: createVNode(ExclamationCircleOutlined),
      content: 'Bạn có chắc chắn muốn cập nhật cấp bậc này?',
      async onOk() {
        try {
          loading.value = true;
          // Validate trước khi cập nhật
          if (!await validateForm()) {
            return;
          }

          const payload: LevelSetting = {
            ...formState.value,
            websiteId: props.websiteId,
          };

          const success = await levelSettingStore.updateLevel(payload);
          if (success) {
            message.success('Cập nhật cấp bậc thành công');
            emit('submit', payload);
            emit('success');
            emit('update:visible', false);
            resetForm();
          }
        } catch (error) {
          if (error instanceof ApiError) {
            message.error(error.message);
          } else {
            message.error('Có lỗi xảy ra khi cập nhật cấp bậc');
          }
        } finally {
          loading.value = false;
        }
      },
      onCancel() {}
    });
  } else {
    handleSubmit();
  }
};

const handleSubmit = async () => {
  try {
    loading.value = true;
    
    if (!await validateForm()) {
      return;
    }

    const payload: LevelSetting = {
      ...formState.value,
      websiteId: props.websiteId,
    };

    if (props.isEdit) {
      const success = await levelSettingStore.updateLevel(payload);
      if (success) {
        message.success('Cập nhật cấp bậc thành công');
        emit('submit', payload);
        emit('success');
        emit('update:visible', false);
        resetForm();
      }
    } else {
      // Xử lý thêm mới
      try {
        await levelSettingStore.createLevelSetting({
          ...payload,
          levelId: undefined  // Đảm bảo không gửi levelId khi tạo mới
        });
        message.success('Thêm cấp bậc mới thành công');
        emit('success');
        emit('update:visible', false);
        resetForm();
      } catch (error) {
        if (error instanceof ApiError) {
          message.error(error.message);
        } else {
          message.error('Có lỗi xảy ra khi thêm cấp bậc mới');
        }
      }
    }
  } catch (error) {
    if (error instanceof ApiError) {
      message.error(error.message);
    } else {
      message.error('Có lỗi xảy ra khi xử lý');
    }
  } finally {
    loading.value = false;
  }
};

const resetForm = () => {
  formRef.value?.resetFields();
  errors.value = {};
  formState.value = { ...defaultFormState, websiteId: props.websiteId };
};

const handleCancel = () => {
  resetForm();
  emit('update:visible', false);
};

// Watch khi initialData thay đổi để cập nhật formState
watch(
  () => props.initialData,
  (newVal) => {
    if (newVal) {
      formState.value = { ...newVal };
    } else {
      formState.value = { ...defaultFormState, websiteId: props.websiteId };
    }
  },
  { immediate: true, deep: true }
);

// Watch khi visible thay đổi
watch(
  () => props.visible,
  (newVal) => {
    if (newVal && props.isEdit && props.initialData) {
      formState.value = { ...props.initialData };
    }
  }
);
</script>

<style>
/* Override chỉ button primary không phải dangerous */
button.ant-btn.ant-btn-primary:not(.ant-btn-dangerous) {
  -webkit-appearance: none;
  background-color: #1890ff !important;
  background-image: none;
}

/* Ensure modal confirm buttons (không phải dangerous) cũng được override */
.ant-modal-confirm .ant-btn.ant-btn-primary:not(.ant-btn-dangerous),
.ant-modal-footer .ant-btn.ant-btn-primary:not(.ant-btn-dangerous) {
  -webkit-appearance: none;
  background-color: #1890ff !important;
  background-image: none;
}
</style>

